mapper.select:
select ab.* from hp_adv_banner ab 
where 1=1 
AND ab.use_on=#{useOn}
AND del_on=#{delOn}

	 AND ab.end_time >=#{curTime} 
	 AND ab.end_time <#{curTime} 

ORDER BY ab.del_on,ab.use_on desc,ab.sort_num 
LIMIT #{currentResult},#{showCount}

select COUNT(*) from hp_adv_banner ab 
where 1=1 
AND ab.use_on=#{useOn}
AND del_on=#{delOn}

	 AND ab.end_time >=#{curTime} 
	 AND ab.end_time <#{curTime}
