mapper.select:
		SELECT p.hp_position_id,p.hp_company_id,c.com_name,p.pos_name,ps.lower_num,ps.hight_num,aco.county_name,aci.city_name,
		p.ret_on,p.ret_man_money,p.ret_woman_money,p.urgent_on,p.urgent_money,pr.*,
		(SELECT GROUP_CONCAT(pw.welfare_name) FROM hp_position_ref_welfare prw JOIN hp_position_welfare pw 
         WHERE prw.hp_position_id=p.hp_position_id AND prw.hp_position_welfare_id=pw.hp_position_welfare_id) welfareArr,
         (SELECT COUNT(pru.hp_position_ref_user_id) FROM hp_position_group pg JOIN hp_position_ref_user pru 
		WHERE pg.hp_position_id=p.hp_position_id AND p.group_on=1 AND pg.hp_position_group_id=pru.hp_position_group_id) groupSccNum
		FROM hp_position p JOIN hp_company c ON p.hp_company_id=c.hp_company_id 
		JOIN hp_position_salary ps ON p.hp_position_salary_id=ps.hp_position_salary_id 
		JOIN hp_area_county aco ON p.county_id=aco.county_id 
		JOIN hp_area_city aci ON aco.city_id=aci.city_id 
		JOIN hp_position_require pr ON p.hp_position_require_id=pr.hp_position_require_id 
		WHERE p.start_time >=#{curTime} AND p.end_time <=#{curTime}  AND p.pos_state=1 
		AND p.hot_on=#{hotOn}
		AND p.pos_nature=#{posNature}
		AND p.ret_on=#{retOn}
		AND p.welfare_on=#{welfareOn}
		AND p.urgent_on=#{urgentOn}
		AND p.group_on=#{groupOn}
		AND aci.city_name==#{cityName}

			AND (p.pos_type LIKE CONCAT('%',#{keyWord},'%') OR p.pos_name LIKE CONCAT('%',#{keyWord},'%') OR c.com_name LIKE CONCAT('%',#{keyWord},'%') )

		ORDER BY p.apply_time desc

SELECT COUNT(*) FROM hp_position_ref_user pru JOIN hp_position p ON pru.hp_position_id=p.hp_position_id 
WHERE pru.part_type=1 
 AND pru.hp_position_id=#{hpPositionId} 

	AND p.start_time <=#{curTime} AND p.end_time >=#{curTime} 
	AND pru.work_on=1 
	AND p.end_time <#{curTime}  

AND pru.hp_user_id=

SELECT COUNT(*) FROM hp_position_ref_user pru JOIN hp_position_group pg 
ON pru.hp_position_group_id=pg.hp_position_group_id 
WHERE pru.part_type=2 
 AND pg.hp_position_id=#{hpPositionId} 

	AND pg.start_time <=#{curTime} AND pg.end_time >=#{curTime} 
	AND pru.work_on=1 
	AND (SELECT COUNT(*) FROM hp_position_ref_user pru1 WHERE pru1.hp_position_group_id=pru.hp_position_group_id )>=3 
	AND p.end_time <#{curTime} 

AND pru.hp_user_id=
