mapper.select:
SELECT p.hp_position_id,p.hp_company_id,p.pos_name,p.pos_state,p.start_time,p.end_time,
c.approve_state,c.com_name,p.hot_on 
FROM hp_position p JOIN hp_company c ON p.hp_company_id=c.hp_company_id 
WHERE 1=1  and p.del_on !=  1
AND p.pos_name LIKE CONCAT('%',#{posName},'%') 
AND c.com_name LIKE CONCAT('%',#{comName},'%')
AND p.apply_time >=#{creatStartTime}
AND p.apply_time <=#{creatEndTime}

	AND p.start_time <=#{curTime} AND p.end_time >=#{curTime} 
	AND p.end_time <#{curTime}  

ORDER BY p.apply_time desc,p.hp_position_id desc

SELECT p.*,c.com_name,ps.lower_num,ps.hight_num,aco.county_name,aci.city_name,c.com_logo,, c.approve_state approveState,ct.type_name,cs.lower_num scale_lower,cs.hight_num scale_high,c.addr_detail,c.com_location, (SELECT GROUP_CONCAT(pw.welfare_name) FROM hp_position_ref_welfare prw JOIN hp_position_welfare pw WHERE prw.hp_position_id=p.hp_position_id AND prw.hp_position_welfare_id=pw.hp_position_welfare_id) welfareArr
,(SELECT pru.hp_position_group_id FROM hp_position_ref_user pru WHERE pru.hp_position_id = p.hp_position_id AND pru.hp_user_id= ORDER BY pru.hp_position_ref_user_id DESC LIMIT 1 ) hpPositionGroupId
FROM hp_position p JOIN hp_company c ON p.hp_company_id=c.hp_company_id JOIN hp_position_salary ps ON p.hp_position_salary_id=ps.hp_position_salary_id JOIN hp_area_county aco ON p.county_id=aco.county_id JOIN hp_area_city aci ON aco.city_id=aci.city_id LEFT JOIN hp_position_require pr ON p.hp_position_id=pr.hp_position_id LEFT JOIN hp_company_type ct ON c.hp_company_type_id=ct.hp_company_type_id LEFT JOIN hp_company_scale cs ON c.hp_company_scale_id=cs.hp_company_scale_id WHERE p.hp_position_id=#{hpPositionId}

SELECT COUNT(DISTINCT pru.hp_position_id) FROM hp_position_ref_user pru JOIN hp_position p ON pru.hp_position_id=p.hp_position_id 
WHERE 1=1 
 AND pru.part_type=#{partType} 
 AND pru.hp_position_id=#{hpPositionId} 

	AND p.start_time <=#{curTime} AND p.end_time >=#{curTime} 
	AND pru.work_on=1 
	AND p.end_time <#{curTime}  

AND pru.hp_user_id=

SELECT COUNT(DISTINCT pg.hp_position_id) FROM hp_position_ref_user pru JOIN hp_position_group pg 
ON pru.hp_position_group_id=pg.hp_position_group_id 
WHERE pru.part_type=2 
 AND pg.hp_position_id=#{hpPositionId} 
 AND pg.hp_position_group_id=#{hpPositionGroupId} 

	AND pg.start_time <=#{curTime} AND pg.end_time >=#{curTime} 
	AND pru.work_on=1 
	AND (SELECT COUNT(*) FROM hp_position_ref_user pru1 WHERE pru1.hp_position_group_id=pru.hp_position_group_id )>=3 
	AND p.end_time <#{curTime} 
	AND FROM_UNIXTIME(pru.part_time,'%Y-%m-%d')=FROM_UNIXTIME(#{curTime},'%Y-%m-%d') 

AND pru.hp_user_id=

SELECT p.pos_name 
FROM hp_position_group gp JOIN hp_position p ON gp.hp_position_id=p.hp_position_id 
WHERE pr.hp_position_group_id=#{hpPositionGroupId}
