mapper.select:
SELECT us.* FROM hp_user_search us 
WHERE 1=1
AND us.hp_user_bound_id=
AND us.del_on=#{delOn} 
AND us.content=#{keyWord} 
ORDER BY us.time DESC,us.num DESC 

	LiMIT #{currentResult},#{showCount}

SELECT COUNT(*) FROM hp_user_search us 
WHERE 1=1
AND us.hp_user_bound_id=
AND us.del_on=#{delOn} 
AND us.content=#{keyWord}

mapper.update:
UPDATE hp_user_search us SET us.del_on=1 
WHERE us.hp_user_bound_id= 
AND us.hp_user_search_id=#{hpUserSearchId}
